def parse_html(text, item):
    tree = etree.HTML(text)
    title_list = tree.xpath('//div[@class="item"]/img/@alt')
    src_list = tree.xpath('//div[@class="item"]/img/@src')
    for i in range(len(title_list)):
        title = title_list[i]
        src = src_list[i]
        item.append({
            'Image Title': title,
            'Image URL': src
        })
